import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Dumbbell, Activity, Ruler, Calendar, Plus, X, Search, ChevronDown, ChevronRight } from 'lucide-react';
import { supabase } from '../supabase';
import { BIBLIOTECA_EXERCICIOS } from '../data/bibliotecaExercicios';

const MontarTreino = () => {
  const { alunoId } = useParams();
  const navigate = useNavigate();
  const [aluno, setAluno] = useState(null);
  const [loading, setLoading] = useState(true);
  const [treino, setTreino] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchAluno();
  }, [alunoId]);

  const fetchAluno = async () => {
    try {
      const { data, error } = await supabase
        .from('perfis')
        .select('*')
        .eq('id', alunoId)
        .eq('tipo', 'aluno')
        .single();

      if (error) {
        console.error('Erro ao buscar aluno:', error);
        navigate('/admin/dashboard');
      } else {
        setAluno(data);
      }
    } catch (error) {
      console.error('Erro:', error);
      navigate('/admin/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const handleBack = () => {
    navigate('/admin/dashboard');
  };

  // Biblioteca de exercícios (mock para exemplo)
  const bibliotecaExercicios = [
    { id: 1, nome: 'Supino Reto', grupo: 'Peito', equipamento: 'Barra' },
    { id: 2, nome: 'Agachamento Livre', grupo: 'Pernas', equipamento: 'Barra' },
    { id: 3, nome: 'Rosca Direta', grupo: 'Bíceps', equipamento: 'Halteres' },
    { id: 4, nome: 'Desenvolvimento Militar', grupo: 'Ombros', equipamento: 'Barra' },
    { id: 5, nome: 'Cadeira Extensora', grupo: 'Quadríceps', equipamento: 'Máquina' },
    { id: 6, nome: 'Puxada na Barra', grupo: 'Costas', equipamento: 'Barra Fixa' },
    { id: 7, nome: 'Leg Press 45°', grupo: 'Pernas', equipamento: 'Máquina' },
    { id: 8, nome: 'Elevação Lateral', grupo: 'Ombros', equipamento: 'Halteres' },
    { id: 9, nome: 'Tríceps Testa', grupo: 'Tríceps', equipamento: 'Halteres' },
    { id: 10, nome: 'Cadeira Flexora', grupo: 'Posteriores', equipamento: 'Máquina' },
    { id: 11, nome: 'Crucifixo Inclinado', grupo: 'Peito', equipamento: 'Halteres' },
    { id: 12, nome: 'Stiff', grupo: 'Posteriores', equipamento: 'Barra' }
  ];

  const exerciciosFiltrados = bibliotecaExercicios.filter(exercicio =>
    exercicio.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
    exercicio.grupo.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const adicionarExercicio = (exercicio) => {
    const novoItem = {
      ...exercicio,
      treinoId: Date.now(),
      series: 3,
      repeticoes: 12,
      descanso: 60,
      observacoes: ''
    };
    setTreino([...treino, novoItem]);
  };

  const removerExercicio = (treinoId) => {
    setTreino(treino.filter(item => item.treinoId !== treinoId));
  };

  const atualizarExercicio = (treinoId, campo, valor) => {
    setTreino(treino.map(item => 
      item.treinoId === treinoId 
        ? { ...item, [campo]: valor }
        : item
    ));
  };

  if (loading) {
    return (
      <div className="w-full h-screen bg-slate-950 flex items-center justify-center">
        <div className="text-white">Carregando dados do aluno...</div>
      </div>
    );
  }

  if (!aluno) {
    return (
      <div className="w-full h-screen bg-slate-950 flex items-center justify-center">
        <div className="text-white">Aluno não encontrado</div>
      </div>
    );
  }

  return (
    <div className="w-full h-screen bg-slate-950 flex flex-col">
      {/* Cabeçalho */}
      <header className="bg-slate-900 border-b border-slate-800 p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            {/* Botão Voltar */}
            <button
              onClick={handleBack}
              className="flex items-center space-x-2 text-gray-400 hover:text-white transition-colors duration-200 group"
            >
              <ArrowLeft 
                size={20} 
                className="group-hover:-translate-x-1 transition-transform duration-200"
              />
              <span>Voltar</span>
            </button>

            {/* Nome do Aluno */}
            <div>
              <h1 className="text-2xl font-bold text-white flex items-center space-x-2">
                <Dumbbell size={24} className="text-emerald-500" />
                <span>{aluno.nome}</span>
              </h1>
              <div className="flex items-center space-x-6 mt-2">
                {/* Peso */}
                <div className="flex items-center space-x-2">
                  <Activity size={16} className="text-blue-500" />
                  <span className="text-gray-300 text-sm">
                    {aluno.peso ? `${aluno.peso} kg` : 'Não informado'}
                  </span>
                </div>

                {/* Altura */}
                <div className="flex items-center space-x-2">
                  <Ruler size={16} className="text-emerald-500" />
                  <span className="text-gray-300 text-sm">
                    {aluno.altura ? `${aluno.altura} cm` : 'Não informado'}
                  </span>
                </div>

                {/* Idade */}
                <div className="flex items-center space-x-2">
                  <Calendar size={16} className="text-purple-500" />
                  <span className="text-gray-300 text-sm">
                    {aluno.idade ? `${aluno.idade} anos` : 'Não informado'}
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Info do Treino */}
          <div className="text-right">
            <p className="text-gray-400 text-sm">Exercícios no treino</p>
            <p className="text-2xl font-bold text-white">{treino.length}</p>
          </div>
        </div>
      </header>

      {/* Área de Trabalho */}
      <div className="flex-1 flex flex-col lg:flex-row overflow-hidden">
        {/* Lado Esquerdo - Biblioteca de Exercícios */}
        <div className="w-full lg:w-1/2 bg-slate-900 border-r border-slate-800 flex flex-col">
          <div className="p-6 border-b border-slate-800">
            <h2 className="text-lg font-semibold text-white mb-4">Biblioteca de Exercícios</h2>

            {/* Busca */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={18} />
              <input
                type="text"
                placeholder="Buscar exercícios..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 bg-slate-800 border border-slate-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
              />
            </div>
          </div>

          {/* Lista de Exercícios */}
          <div className="flex-1 overflow-y-auto p-6">
            <div className="space-y-3">
              {exerciciosFiltrados.map((exercicio) => (
                <div
                  key={exercicio.id}
                  className="bg-slate-800 rounded-lg p-4 hover:bg-slate-700 transition-colors duration-200 cursor-pointer"
                  onClick={() => adicionarExercicio(exercicio)}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-white font-medium">{exercicio.nome}</h3>
                      <div className="flex items-center space-x-3 mt-1">
                        <span className="text-xs bg-blue-600/20 text-blue-400 px-2 py-1 rounded">
                          {exercicio.grupo}
                        </span>
                        <span className="text-xs bg-gray-600/20 text-gray-400 px-2 py-1 rounded">
                          {exercicio.equipamento}
                        </span>
                      </div>
                    </div>
                    <Plus size={20} className="text-emerald-500" />
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Lado Direito - Lista do Treino */}
        <div className="w-full lg:w-1/2 bg-slate-950 flex flex-col">
          <div className="p-6 border-b border-slate-800">
            <h2 className="text-lg font-semibold text-white">Treino do Dia</h2>
          </div>

          <div className="flex-1 overflow-y-auto p-6">
            {treino.length === 0 ? (
              <div className="text-center py-12">
                <Dumbbell size={48} className="text-gray-600 mx-auto mb-4" />
                <p className="text-gray-400 text-lg mb-2">Nenhum exercício adicionado</p>
                <p className="text-gray-500 text-sm">
                  Clique nos exercícios da biblioteca para começar a montar o treino
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {treino.map((item, index) => (
                  <div key={item.treinoId} className="bg-slate-900 rounded-lg p-4 border border-slate-800">
                    <div className="flex items-start justify-between mb-3">
                      <div className="flex-1">
                        <h3 className="text-white font-medium">{item.nome}</h3>
                        <div className="flex items-center space-x-3 mt-1">
                          <span className="text-xs bg-blue-600/20 text-blue-400 px-2 py-1 rounded">
                            {item.grupo}
                          </span>
                          <span className="text-xs bg-gray-600/20 text-gray-400 px-2 py-1 rounded">
                            {item.equipamento}
                          </span>
                        </div>
                      </div>
                      <button
                        onClick={() => removerExercicio(item.treinoId)}
                        className="p-1 text-red-400 hover:text-red-300 hover:bg-red-900/20 rounded transition-colors duration-200"
                      >
                        <X size={18} />
                      </button>
                    </div>

                    <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                      <div>
                        <label className="block text-xs text-gray-400 mb-1">Séries</label>
                        <input
                          type="number"
                          min="1"
                          max="10"
                          value={item.series}
                          onChange={(e) => atualizarExercicio(item.treinoId, 'series', parseInt(e.target.value))}
                          className="w-full px-2 py-1 bg-slate-800 border border-slate-700 rounded text-white text-sm focus:outline-none focus:ring-1 focus:ring-emerald-500"
                        />
                      </div>

                      <div>
                        <label className="block text-xs text-gray-400 mb-1">Repetições</label>
                        <input
                          type="number"
                          min="1"
                          max="50"
                          value={item.repeticoes}
                          onChange={(e) => atualizarExercicio(item.treinoId, 'repeticoes', parseInt(e.target.value))}
                          className="w-full px-2 py-1 bg-slate-800 border border-slate-700 rounded text-white text-sm focus:outline-none focus:ring-1 focus:ring-emerald-500"
                        />
                      </div>

                      <div>
                        <label className="block text-xs text-gray-400 mb-1">Descanso (s)</label>
                        <input
                          type="number"
                          min="0"
                          max="600"
                          value={item.descanso}
                          onChange={(e) => atualizarExercicio(item.treinoId, 'descanso', parseInt(e.target.value))}
                          className="w-full px-2 py-1 bg-slate-800 border border-slate-700 rounded text-white text-sm focus:outline-none focus:ring-1 focus:ring-emerald-500"
                        />
                      </div>

                      <div>
                        <label className="block text-xs text-gray-400 mb-1">Ordem</label>
                        <input
                          type="text"
                          value={index + 1}
                          readOnly
                          className="w-full px-2 py-1 bg-slate-700 border border-slate-600 rounded text-gray-300 text-sm"
                        />
                      </div>
                    </div>

                    <div className="mt-3">
                      <label className="block text-xs text-gray-400 mb-1">Observações</label>
                      <textarea
                        value={item.observacoes}
                        onChange={(e) => atualizarExercicio(item.treinoId, 'observacoes', e.target.value)}
                        placeholder="Instruções especiais..."
                        className="w-full px-3 py-2 bg-slate-800 border border-slate-700 rounded text-white text-sm placeholder-gray-500 focus:outline-none focus:ring-1 focus:ring-emerald-500 resize-none"
                        rows="2"
                      />
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Área de Ações */}
          {treino.length > 0 && (
            <div className="p-6 border-t border-slate-800">
              <div className="flex space-x-4">
                <button className="flex-1 bg-gray-600 hover:bg-gray-700 text-white py-3 px-4 rounded-lg transition-colors duration-200">
                  Salvar Rascunho
                </button>
                <button className="flex-1 bg-emerald-600 hover:bg-emerald-700 text-white py-3 px-4 rounded-lg transition-colors duration-200">
                  Finalizar Treino
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MontarTreino;
