import React, { useState } from 'react';
import { Dumbbell, Calendar, Clock, X, ChevronRight } from 'lucide-react';

const TreinosAluno = ({ treinos, onTreinoClick }) => {
  const [selectedTreino, setSelectedTreino] = useState(null);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('pt-BR', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleCardClick = (treino) => {
    setSelectedTreino(treino);
    onTreinoClick(treino);
  };

  const getExerciciosCount = (exercicios) => {
    return Array.isArray(exercicios) ? exercicios.length : 0;
  };

  if (treinos.length === 0) {
    return (
      <div className="bg-slate-900 rounded-xl p-8 border border-slate-800 text-center">
        <Dumbbell size={48} className="text-gray-600 mx-auto mb-4" />
        <h3 className="text-xl font-semibold text-white mb-2">Nenhum treino encontrado</h3>
        <p className="text-gray-400">
          Seu personal ainda não enviou nenhum treino para você.
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <h2 className="text-xl font-bold text-white mb-4 flex items-center space-x-2">
        <Dumbbell size={24} className="text-emerald-500" />
        <span>Meus Treinos</span>
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {treinos.map((treino) => (
          <div
            key={treino.id}
            onClick={() => handleCardClick(treino)}
            className="bg-slate-900 rounded-xl p-6 border border-slate-800 hover:border-emerald-600 transition-all duration-200 cursor-pointer group hover:shadow-lg hover:shadow-emerald-600/10"
          >
            <div className="flex items-start justify-between mb-4">
              <div className="flex-1">
                <h3 className="text-lg font-semibold text-white mb-2 group-hover:text-emerald-400 transition-colors duration-200">
                  {treino.nome_treino}
                </h3>
                <div className="flex items-center space-x-4 text-sm text-gray-400">
                  <div className="flex items-center space-x-1">
                    <Calendar size={14} />
                    <span>{formatDate(treino.data_criacao)}</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Clock size={14} />
                    <span>{formatTime(treino.data_criacao)}</span>
                  </div>
                </div>
              </div>
              <ChevronRight 
                size={20} 
                className="text-gray-500 group-hover:text-emerald-500 group-hover:translate-x-1 transition-all duration-200" 
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Dumbbell size={16} className="text-emerald-500" />
                <span className="text-gray-300">
                  {getExerciciosCount(treino.exercicios)} exercícios
                </span>
              </div>
              <div className="text-xs bg-emerald-600/20 text-emerald-400 px-2 py-1 rounded">
                Ativo
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TreinosAluno;
